mapper.select:
select 
from authority_user


        and uuid=#{uuid}


        and username = #{username}


        and `password` = #{password}


        and email = #{email}


        and phone = #{phone}


        and nickname = #{nickname}


        and remark = #{remark}


        and valid_time = #{validTime}


        and is_delete = #{isDelete}


        and update_time = #{updateTime}


        and create_time = #{createTime}


        and qq_open_id = #{qqOpenId}
